css:
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0;
    background-color: #f7f7f7;
}

.header {
    text-align: center;
    padding: 20px;
    background-color: #003366;
    color: white;
}

.header h2 {
    font-size: 28px;
    margin: 0;
}

.content {
    margin: 30px auto;
    padding: 20px;
    background-color: white;
    width: 80%;
    border-radius: 8px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
}

.content p {
    font-size: 16px;
    margin: 10px 0;
    line-height: 1.5;
}

.content .title {
    font-weight: bold;
    font-size: 18px;
}

.status {
    font-size: 18px;
    font-weight: bold;
    padding: 10px;
    text-align: center;
    border-radius: 5px;
    margin-top: 20px;
}

.status-overdue {
    background-color: #f44336;
    color: white;
}

.status-returned {
    background-color: #4CAF50;
    color: white;
}

.status-issued {
    background-color: #FF9800;
    color: white;
}

.footer {
    text-align: center;
    padding: 10px;
    background-color: #003366;
    color: white;
    font-size: 14px;
    margin-top: 30px;
}
